function ocutaElementosDoHeader(){document.querySelector("header").classList.add("header--is-hidden")}function exibeElementosDoHeader(){document.querySelector("header").classList.remove("header--is-hidden")}document.addEventListener("DOMContentLoaded",function(){let e=document.querySelector(".hero").clientHeight;window.addEventListener("scroll",function(){(window.scrollY>e?ocutaElementosDoHeader:exibeElementosDoHeader)()})});